mapper.select:
SELECT * FROM A_USER

	
		NAME LIKE '%'||#{name}||'%'
	
	
		AND ISADMIN = #{isadmin}

mapper.insert:
INSERT INTO A_USER
VALUES(#{id},#{pwd},#{name},#{phone},#{img},#{email},SYSDATE,NULL,#{isadmin})

	SELECT * FROM A_USER WHERE ID = #{id}

mapper.update:
UPDATE A_USER

	PWD = #{pwd}, NAME = #{name}, PHONE = #{phone}, IMG =
		#{img}, EMAIL = #{email}
	
	,ISADMIN = #{isadmin},STOP_DATE = NULL
	ISADMIN = #{isadmin}, STOP_DATE = SYSDATE+7

WHERE ID = #{id}
